import React from "react";
import Slider from "react-slick";

import RecentItems from "../recent-items";

var settings = {
  dots: true,
  infinite: false,
  speed: 500,
  slidesToShow: 1,
  slidesToScroll: 1,
  centerMode: true,
  pauseOnHover: true,
  responsive: [
    {
      breakpoint: 1024,
      settings: {
        slidesToShow: 3,
        slidesToScroll: 3,
        infinite: true,
        dots: true,
      },
    },
    {
      breakpoint: 600,
      settings: {
        slidesToShow: 2,
        slidesToScroll: 2,
        initialSlide: 2,
      },
    },
    {
      breakpoint: 480,
      settings: {
        slidesToShow: 1,
        slidesToScroll: 1,
      },
    },
  ],
};

const RecentActivities = () => {
  return (
    <React.Fragment>
      <div className="recent-container">
        <h2>Recent Activities</h2>
        <p className="primary-btn">View All</p>
      </div>

      {/* <div className="recent-item-container"> */}
      <Slider {...settings}>
        <RecentItems />
        <RecentItems />
        <RecentItems />
        <RecentItems />
      </Slider>
      {/* </div> */}
    </React.Fragment>
  );
};

export default RecentActivities;
