import React, { Component } from "react";
import DisplayContent from "../components/contentDisplay";

class ContentScreen extends Component {
  state = {
    postData: [{ id: null }, { title: null }, { date: null }, { body: null }],
  };

  componentDidMount() {
    const { id } = this.props.location.state;
    const { title } = this.props.location.state;
    const { date } = this.props.location.state;
    const { body } = this.props.location.state;

    this.setState({
      title,
    });
  }

  render() {
    const { postData } = this.state;
    return (
      <React.Fragment>
        <h1>{postData.title}</h1>
        {/* <DisplayContent postData={postData} /> */}
      </React.Fragment>
    );
  }
}

export default ContentScreen;
