import React from "react";
import Slider from "react-slick";

import RecentItems from "../recent-items";

var settings = {
  dots: true,
  infinite: false,
  speed: 500,
  slidesToShow: 3,
  slidesToScroll: 1,
  centerMode: true,
  pauseOnHover: true,
  adaptiveHeight: true,
  responsive: [
    {
      breakpoint: 768,
      settings: {
        slidesToShow: 1,
        slidesToScroll: 1,
      },
    },
  ],
};

const RecentActivities = () => {
  return (
    <React.Fragment>
      <div className="recent-container">
        <h2>Recent Activities</h2>
        <p className="primary-btn">View All</p>
      </div>

      <div className="recent-item-container">
        <Slider {...settings}>
          <RecentItems />
          <RecentItems />
          <RecentItems />
          <RecentItems />
        </Slider>
      </div>
    </React.Fragment>
  );
};

export default RecentActivities;
