      },
    ],
  };

  render() {
    var settings = {
      dots: true,
      arrow: false,
      infinite: true,
      speed: 500,
      slidesToShow: 4,
      slidesToScroll: 1,
      centerMode: true,
      pauseOnHover: true,
      adaptiveHeight: true,
      centerPadding: "60px",
      responsive: [
        {
          breakpoint: 768,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1,
          },
        },
      ],
    };

    const { postData } = this.state;
    return (
      <React.Fragment>
        <div className="recent-container">
