import React, { Component } from "react";
import { CSSTransition, TransitionGroup } from "react-transition-group";
import { v4 as uuidv4 } from "uuid";
import Button from "./button";

class ListPosts extends Component {
  state = {
    posts: [
      { id: uuidv4(), title: "Post 1" },
      { id: uuidv4(), title: "Post 2" },
      { id: uuidv4(), title: "Post 3" },
    ],
  };
  render() {
    const { posts } = this.state;
    return (
      <React.Fragment>
        <Button
          title="Click me"
          className="btn-primary"
          onClick={() => {
            const title = prompt("Enter Post");
            if (title) {
              this.setState((state) => ({
                posts: [...state.posts, { id: uuidv4(), title }],
              }));
            }
          }}
        />

        <TransitionGroup className="login-screen">
          {posts.map((post) => (
            <CSSTransition key={post.id} timeout={500} classNames="fade">
              <div>
                <button>asds</button>
                <span>{post.title}</span>
              </div>
            </CSSTransition>
          ))}
        </TransitionGroup>
      </React.Fragment>
    );
  }
}

export default ListPosts;
