import React, { Component } from "react";
import { CSSTransition, TransitionGroup } from "react-transition-group";
import { uuid } from "uuid";
import Button from "./button";

class ListPosts extends Component {
  state = {
    posts: [
      { id: uuid(), title: "Post 1" },
      { id: uuid(), title: "Post 2" },
      { id: uuid(), title: "Post 3" },
    ],
  };
  render() {
    const { posts } = this.state;
    return (
      <React.Fragment>
        <TransitionGroup className="login-screen">
          <CSSTransition key="2" timeout={500} classNames="fade">
            <Button
              title="Click me"
              className="btn-primary"
              onClick={() => {
                const title = prompt("Enter Post");
                if (title) {
                  this.setState((state) => ({
                    posts: [...state.posts, { id: uuid(), title }],
                  }));
                }
              }}
            />
          </CSSTransition>
        </TransitionGroup>
      </React.Fragment>
    );
  }
}

export default ListPosts;
